import React, { useEffect, useState } from "react";
import { Card, Carousel, Col, Container, Row } from "react-bootstrap";
import "../css/newfilm.css";
import axios from "axios";

const Newfilm = () => {
  const [film, setFilm] = useState([]);
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    axios
      .get("https://phim.nguonc.com/api/films/phim-moi-cap-nhat?page=1")
      .then((response) => {
        setFilm(response.data.items);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleSelect = (selectedIndex, e) => {
    setActiveIndex(selectedIndex);
  };

  // Duplicate items for infinite looping effect
  const duplicatedFilm = [...film, ...film.slice(0, 4)]; // Adjust slice as needed for duplication

  return (
    <>
      <Container className="newfilm-content">
        <h1 className="">Phim mới cập nhật</h1>
        <div className="card-slider-container">
          <Carousel
            activeIndex={activeIndex}
            onSelect={handleSelect}
            controls={true}
            indicators={false}
          >
            {duplicatedFilm.map((card, index) => (
              <Carousel.Item key={index}>
                <Container>
                  <Row>
                    <Col sm={3}>
                      <Card className="card">
                        <div className="card-img-container">
                          <Card.Img
                            variant="top"
                            src={card.poster_url}
                            alt={card.name}
                            className="card-img"
                          />
                          <div className="card-title-overlay">
                            <Card.Title>{card.name}</Card.Title>
                          </div>
                        </div>
                      </Card>
                    </Col>
                  </Row>
                </Container>
              </Carousel.Item>
            ))}
          </Carousel>
        </div>
      </Container>
    </>
  );
};

export default Newfilm;
