import React, { useState, useEffect } from "react";
import "../css/banner.css";
import { Container } from "react-bootstrap";
import axios from "axios";

const Banner = () => {
  // const videos = [
  //   "./video/video1.mp4",
  //   "./video/video2.mp4",
  //   "./video/video3.mp4",
  // ];

  // const images = [
  //   "./images/title1.png",
  //   "./images/title2.png",
  //   "./images/title3.png",
  // ];
  const [titleVideos, setTitleVideos] = useState([]);

  useEffect(() => {
    axios
      .get(
        `https://phim.nguonc.com/api/films/search?keyword=One Piece Film: Red`
      )
      .then((response) => {
        setTitleVideos(response.data.items);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  console.log(titleVideos);
  const [selectedVideo, setSelectedVideo] = useState("");
  const [selectedImage, setSelectedImage] = useState("");

  // useEffect(() => {
  //   const randomIndex = Math.floor(Math.random() * videos.length);
  //   setSelectedVideo(videos[randomIndex]);
  //   setSelectedImage(images[randomIndex]);
  // }, []);

  if (!selectedVideo) {
    return null; // Ensure video is selected before rendering
  }

  if (!selectedImage) {
    return null; // Ensure video is selected before rendering
  }

  return (
    <div className="banner-container">
      <video autoPlay loop muted playsInline className="background-clip">
        <source src={selectedVideo} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <Container>
        <div className="description">
          <img className="banner-logo" src={selectedImage} alt="abc" />
          <p>{titleVideos.description}</p>
          <a className="btn play-btn" href="/">
            <i className="bi bi-play-fill"></i> Start
          </a>
          <a className="btn detail-btn" href="/">
            <i className="bi bi-info-circle"></i> Detail
          </a>
        </div>
      </Container>
    </div>
  );
};

export default Banner;
