import axios from "axios";
import React, { useEffect, useState } from "react";

const Listfilm = () => {
  const filmNames = [1];
  const [films, setFilms] = useState([]);

  useEffect(() => {
    const fetchFilms = filmNames.map((name) =>
      axios
        .get(`https://phim.nguonc.com/api/films/quoc-gia/au-my?page=${name}`)
        .then((response) => response.data.items)
    );

    Promise.all(fetchFilms)
      .then((results) => {
        // Flatten the array of arrays into a single array
        const allFilms = results.flat();
        setFilms(allFilms);
      })
      .catch((error) => {
        // console.log(error);
      });
  }, [filmNames]); // Include filmNames in the dependency array
  return (
    <>
      <h1>hello world</h1>
    </>
  );
};

export default Listfilm;
