import React, { useState, useEffect } from "react";
import "../css/banner.css";
import { Container } from "react-bootstrap";
import axios from "axios";

const Banner = () => {
  const [titleVideos, setTitleVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState("");
  const [selectedImage, setSelectedImage] = useState("");
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    axios
      .get(
        `https://phim.nguonc.com/api/films/search?keyword=One%20Piece%20Film%3A%20Red`
      )
      .then((response) => {
        const movies = response.data.items;
        setTitleVideos(movies);
        if (movies.length > 0) {
          setMovie(movies[0]); // Assuming you want to display the first movie
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  

  const videos = [
    "./video/video1.mp4",
    "./video/video2.mp4",
    "./video/video3.mp4",
  ];

  const images = [
    "./images/title1.png",
    "./images/title2.png",
    "./images/title3.png",
  ];

  useEffect(() => {
    const randomIndex = Math.floor(Math.random() * videos.length);
    setSelectedVideo(videos[randomIndex]);
    setSelectedImage(images[randomIndex]);
  }, [videos, images]);

  if (!selectedVideo || !selectedImage || !movie) {
    return null; // Ensure video and movie data are selected before rendering
  }

  return (
    <div className="banner-container">
      <video autoPlay loop muted playsInline className="background-clip">
        <source src={selectedVideo} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <Container>
        <div className="description">
          <img className="banner-logo" src={selectedImage} alt="abc" />
          <h2>{movie.name}</h2>
          <p>{movie.description}</p>
          <a className="btn play-btn" href="/">
            <i className="bi bi-play-fill"></i> Start
          </a>
          <a className="btn detail-btn" href="/">
            <i className="bi bi-info-circle"></i> Detail
          </a>
        </div>
      </Container>
    </div>
  );
};

export default Banner;
