import React, { useEffect, useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import "../css/film.css"; // Import your CSS file
import { useParams } from "react-router-dom";
import axios from "axios";

const Film = () => {
  const { name } = useParams();
  const [film, setFilm] = useState(null);
  const [currentEpisode, setCurrentEpisode] = useState(0);

  useEffect(() => {
    axios
      .get(`https://phim.nguonc.com/api/film/${name}`)
      .then((response) => {
        setFilm(response.data.movie);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [name]);

  const changeEpisode = (index) => {
    setCurrentEpisode(index);
  };

  if (!film) {
    return <p>Loading...</p>;
  }

  return (
    <>
      <Header />
      <div className="film-container">
        <div className="episode-navbar">
          {film.episodes.map((episode, index) => (
            <button
              key={index}
              className={`episode-button ${
                index === currentEpisode ? "active" : ""
              }`}
              onClick={() => changeEpisode(index)}
            >
              Tập {episode.items[index].name}
            </button>
          ))}
        </div>
        <div className="video-container">
          <iframe
            title={`Tập ${film.episodes[currentEpisode].items[0].name}`}
            width="560"
            height="315"
            src={film.episodes[currentEpisode].items[0].embed}
            allowFullScreen
          ></iframe>
        </div>
        <div className="test">
          {film.episodes.map((episode, index) => {
            <p key={index}> {episode.items[0].name}</p>;
          })}
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Film;
