import React from "react";
import { Carousel, Col, Container } from "react-bootstrap";

const Newfilm = () => {
    const cards = [
        {
            id: 1,
            title: 'Card 1',
            text: 'This is the first card.',
            image: 'https://via.placeholder.com/300x200'
        },
        {
            id: 2,
            title: 'Card 2',
            text: 'This is the second card.',
            image: 'https://via.placeholder.com/300x200'
        },
        {
            id: 3,
            title: 'Card 3',
            text: 'This is the third card.',
            image: 'https://via.placeholder.com/300x200'
        },
        {
            id: 4,
            title: 'Card 4',
            text: 'This is the fourth card.',
            image: 'https://via.placeholder.com/300x200'
        },
        {
            id: 5,
            title: 'Card 5',
            text: 'This is the fifth card.',
            image: 'https://via.placeholder.com/300x200'
        },
        {
            id: 6,
            title: 'Card 6',
            text: 'This is the sixth card.',
            image: 'https://via.placeholder.com/300x200'
        }
    ];

    const chunkArray = (arr, size) => {
        const result = [];
        for (let i = 0; i < arr.length; i += size) {
            result.push(arr.slice(i, i + size));
        }
        return result;
    };

    const cardChunks = chunkArray(cards, 3); // Adjust the number 3 to display more or fewer cards per slide

    return (
        <div className="card-slider-container">
            <Carousel>
                {cardChunks.map((chunk, index) => (
                    <Carousel.Item key={index}>
                        <Container>
                            <Row>
                                {chunk.map(card => (
                                    <Col key={card.id} sm={4}>
                                        <Card className="card">
                                            <Card.Img variant="top" src={card.image} alt={card.title} />
                                            <Card.Body>
                                                <Card.Title>{card.title}</Card.Title>
                                                <Card.Text>{card.text}</Card.Text>
                                            </Card.Body>
                                        </Card>
                                    </Col>
                                ))}
                            </Row>
                        </Container>
                    </Carousel.Item>
                ))}
            </Carousel>
        </div>
    );
};

export default Newfilm;
