import React, { useState, useEffect } from "react";
import "../css/banner.css";
import Header from "./Header";
import { Container } from "react-bootstrap";
import "bootstrap-icons/font/bootstrap-icons.css";

const Banner = () => {
  const videos = [
    "./video/video1.mp4",
    "./video/video2.mp4",
    "./video/video3.mp4",
  ];

  const [selectedVideo, setSelectedVideo] = useState("");

  useEffect(() => {
    const randomIndex = Math.floor(Math.random() * videos.length);
    setSelectedVideo(videos[randomIndex]);
  }, []);

  if (!selectedVideo) {
    return null; // Ensure video is selected before rendering
  }

  return (
    <div className="banner-container">
      <video autoPlay loop muted playsInline className="background-clip">
        <source src={selectedVideo} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <Container className="d-flex align-items-end">
        <p className="description">
          Trong một thế giới mà những thợ săn, những con người sở hữu khả năng
          phép thuật, phải chiến đấu với những con quái vật chết người để bảo vệ
          loài người khỏi sự hủy diệt, một thợ săn nổi tiếng yếu ớt tên là Sung
          Jin-woo thấy mình ở trong một cuộc đấu tranh sinh tồn dường như bất
          tận.
        </p>
        <a className="btn play-btn" href="/">
          <i class="bi bi-play-fill">Start</i>
        </a>
        <a className="btn detail-btn" href="/">
          <i class="bi bi-info-circle">Detail</i>
        </a>
      </Container>
    </div>
  );
};

export default Banner;
