import React, { useState, useEffect } from "react";
import "../css/banner.css";
import { Container } from "react-bootstrap";
import axios from "axios";

const Banner = () => {
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [selectedVideo, setSelectedVideo] = useState("");
  const [selectedImage, setSelectedImage] = useState("");

  const videoSources = [
    { src: "./video/video1.mp4", keyword: "Khu Vườn Ngôn Từ" },
    { src: "./video/video2.mp4", keyword: "One Piece Film: Red" },
    { src: "./video/video3.mp4", keyword: "Tôi Thăng Cấp Một Mình" },
  ];

  const imageSources = [
    { src: "./images/title1.png", keyword: "Khu Vườn Ngôn Từ" },
    { src: "./images/title2.png", keyword: "One Piece Film: Red" },
    { src: "./images/title3.png", keyword: "Tôi Thăng Cấp Một Mình" },
  ];

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const [response1, response2, response3] = await axios.all([
          axios.get(
            `https://phim.nguonc.com/api/films/search?keyword=Khu%20Vườn%20Ngôn%20Từ`
          ),
          axios.get(
            `https://phim.nguonc.com/api/films/search?keyword=One%20Piece%20Film%3A%20Red`
          ),
          axios.get(
            `https://phim.nguonc.com/api/films/search?keyword=Tôi%20Thăng%20Cấp%20Một%20Mình`
          ),
        ]);

        const allMovies = [
          ...response1.data.items,
          ...response2.data.items,
          ...response3.data.items,
        ];
        setMovies(allMovies);

        if (allMovies.length > 0) {
          const randomIndex = Math.floor(Math.random() * allMovies.length);
          const selectedMovie = allMovies[randomIndex];
          setSelectedMovie(selectedMovie);

          const selectedVideo = videoSources.find(
            (video) => video.keyword === selectedMovie.name
          );
          setSelectedVideo(
            selectedVideo ? selectedVideo.src : videoSources[0].src
          );

          const selectedImage = imageSources.find(
            (image) => image.keyword === selectedMovie.name
          );
          setSelectedImage(
            selectedImage ? selectedImage[randomIndex].src : imageSources[0].src
          );

          // Logging for debugging
          console.log("Selected Movie:", selectedMovie);
          console.log("Selected Video:", selectedVideo);
          console.log("Selected Image:", selectedImage);
        }
      } catch (error) {
        console.log(error);
      }
    };

    fetchMovies();
  }, []);

  if (!selectedVideo || !selectedImage || !selectedMovie) {
    return null; // Ensure video and movie data are selected before rendering
  }

  return (
    <div className="banner-container">
      <video autoPlay loop muted playsInline className="background-clip">
        <source src={selectedVideo} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <Container>
        <div className="description">
          <img className="banner-logo" src={selectedImage} alt="abc" />
          <h2>{selectedMovie.name}</h2>
          <p>{selectedMovie.description}</p>
          <a className="btn play-btn" href="/">
            <i className="bi bi-play-fill"></i> Start
          </a>
          <a className="btn detail-btn" href="/">
            <i className="bi bi-info-circle"></i> Detail
          </a>
        </div>
      </Container>
    </div>
  );
};

export default Banner;
