import React from "react";
import Header from "./Header";
import Footer from "./Footer";

const Film = () => {
    const { name } = useParams();
  const [film, setFilm] = useState(null);

  useEffect(() => {
    axios
      .get(`https://phim.nguonc.com/api/film/${name}`)
      .then((response) => {
        setFilm(response.data.movie);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [name]);

  if (!film) {
    return <p>Loading...</p>;
  }
  return (
    <>
      <Header />
      <Footer />
    </>
  );
};

export default Film;
