import axios from "axios";
import React, { useEffect, useState } from "react";
import { Button, Col, Container, Form, Row } from "react-bootstrap";
import { useParams } from "react-router-dom";
import useAuth from "../hooks/useAuth";
import "../css/comment.css";

const Comment = () => {
  const [comment, setComment] = useState("");
  const [comments, setComments] = useState([]);
  const [users, setUser] = useState("");
  const { id } = useParams();
  const { userId } = useAuth();

  const handleSave = (e) => {
    e.preventDefault(); // Prevent default form submission behavior
    axios
      .post("http://localhost:9999/comments", {
        user_id: userId,
        blog_id: parseInt(id),
        content: comment,
      })
      .then((response) => {
        console.log("New comment created:", response.data);
        setComment("");
        fetchComments(); // Refresh the comments list
      })
      .catch((error) => {
        console.error("There was a problem with the axios operation:", error);
      });
  };

  const fetchComments = () => {
    axios
      .get(`http://localhost:9999/comments/?blog_id=${id}`)
      .then((response) => {
        setComments(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    fetchComments();
  }, [id]);

  useEffect(() => {
    // Fetch all blog posts
    axios
      .get("http://localhost:9999/users")
      .then((response) => {
        set(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  console.log(comments);

  return (
    <>
      <Container>
        <Form onSubmit={handleSave} className="my-3">
          <Form.Group className="mb-3" controlId="formBasicComment">
            <Form.Control
              type="text"
              placeholder="Comment here......"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              required
            />
          </Form.Group>
        </Form>

        <h3>Comments</h3>
        <ul className="comment-list" style={{ color: "white" }}>
          {comments.map((comment) => {
            const user = users.find((user) => user.id == todo.userId);
            return (
              <li className="comment-item" key={comment.id}>
                <img src="../images/defaultuser.png" alt="avatar" />
                <div className="comment-infor">
                  <p style={{ color: "aqua" }}>By User ID {comment.user_id}</p>
                  <p>{comment.content}</p>
                </div>
              </li>
            );
          })}
        </ul>
      </Container>
    </>
  );
};

export default Comment;
