import React, { useEffect, useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import { Col, Container, Row } from "react-bootstrap";
import axios from "axios";
import { Link, useParams } from "react-router-dom";

const Detailblog = () => {
  const [blog, setBlog] = useState("");
  const [top3item, setTop3item] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    // Fetch all blog posts
    axios
      .get(`http://localhost:9999/posts/?id=${id}`)
      .then((response) => {
        setBlog(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  useEffect(() => {
    // Fetch all blog posts
    axios
      .get("http://localhost:9999/posts")
      .then((response) => {
        setBlog(response.data);
      })
      .catch((error) => {
        console.log(error);
      });

    // Fetch top 3 newest posts
    const fetchTop3Items = async () => {
      try {
        const response = await axios.get("http://localhost:9999/posts");
        const sortedItems = response.data.sort((a, b) => b.id - a.id);
        setTop3item(sortedItems.slice(0, 3));
      } catch (error) {
        console.error("Error fetching top 3 items:", error);
      }
    };

    fetchTop3Items();
  }, []);
  return (
    <>
      <Header />
      <Container>
        <Row>
          <Col className="col-9">
            
          </Col>
          <Col md={3}>
            <div className="recent-posts">
              <h2>Recent Posts</h2>
              {top3item.map((post) => (
                <div key={post.id} className="mb-3">
                  <div className="card">
                    <img
                      src={post.img_url}
                      className="card-img-top"
                      alt="Recent post"
                    />
                    <div className="card-body">
                      <h5 className="card-title">{post.title}</h5>
                      <Link to={`/`} className="btn btn-primary">
                        Read more
                      </Link>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </Col>
        </Row>
      </Container>
      <Footer />
    </>
  );
};

export default Detailblog;
