  //       })
  //       .catch((error) => {
  //         console.log(error);
  //       });
  //   }, []);

  useEffect(() => {
    // Create an array of promises for each API request
    const fetchFilms = filmNames.map((name) =>
      axios
        .get(`https://phim.nguonc.com/api/films/quoc-gia/au-my?page={}`)
        .then((response) => response.data.movie)
    );

    // Use Promise.all to wait for all the requests to complete
    Promise.all(fetchFilms)
      .then((filmsData) => {
        setFilm(filmsData);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [filmNames]);

  console.log(film);

  return (
    <>
      <h1>hello world</h1>
    </>
  );
};

export default Listfilm;
