import React, { useEffect, useState } from "react";
import { Card, Carousel, Col, Container, Row } from "react-bootstrap";
import "../css/newfilm.css";
import axios from "axios";

const Newfilm = () => {
  const [films, setFilms] = useState([]);

  useEffect(() => {
    axios
      .get("https://phim.nguonc.com/api/films/phim-moi-cap-nhat?page=1")
      .then((response) => {
        setFilms(response.data.items);
      })
      .catch((error) => {
        console.log("Error fetching films:", error);
      });
  }, []);

  const chunkArray = (arr, size) => {
    const result = [];
    for (let i = 0; i < arr.length; i += size) {
      result.push(arr.slice(i, i + size));
    }
    return result;
  };

  const filmChunks = chunkArray(films, 4); // Adjust the number 4 to display more or fewer films per slide

  return (
    <>
      <Container className="newfilm-content">
        <h1 className="text-center mb-4">Phim mới cập nhật</h1>
        <div className="card-slider-container">
          <Carousel controls indicators={false}>
            {filmChunks.map((chunk, index) => (
              <Carousel.Item key={index}>
                <Container>
                  <Row>
                    {chunk.map((film) => (
                      <Col key={film.slug} sm={3}>
                        <Card className="card">
                          <div className="card-img-container">
                            <Card.Img
                              variant="top"
                              src={film.poster_url} // Assuming film.poster_url contains the URL of the poster image
                              alt={film.name} // Assuming film.name is the title of the film
                              className="card-img"
                            />
                            <div className="card-title-overlay">
                              <Card.Title>{film.name}</Card.Title>
                            </div>
                          </div>
                        </Card>
                      </Col>
                    ))}
                  </Row>
                </Container>
              </Carousel.Item>
            ))}
          </Carousel>
        </div>
      </Container>
    </>
  );
};

export default Newfilm;
