import React, { useEffect } from "react";
import { Card, Carousel, Col, Container, Row } from "react-bootstrap";
import "../css/newfilm.css";
import axios from "axios";

const Newfilm = () => {
  const [film, setFilm] = use
  useEffect(() => {
    axios
      .get("https://phim.nguonc.com/api/films/phim-moi-cap-nhat?page=1")
      .then((response) => {
        setDepartments(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const chunkArray = (arr, size) => {
    const result = [];
    for (let i = 0; i < arr.length; i += size) {
      result.push(arr.slice(i, i + size));
    }
    return result;
  };

  const cardChunks = chunkArray(cards, 4); // Adjust the number 3 to display more or fewer cards per slide

  return (
    <>
      <Container className="newfilm-content">
        <h1 className="">Phim mới cập nhật</h1>
        <div className="card-slider-container">
          <Carousel controls={true} indicators={false}>
            {cardChunks.map((chunk, index) => (
              <Carousel.Item key={index}>
                <Container>
                  <Row>
                    {chunk.map((card) => (
                      <Col key={card.id} sm={3}>
                        <Card className="card">
                          <div className="card-img-container">
                            <Card.Img
                              variant="top"
                              src="./images/product1.jpg"
                              alt={card.title}
                              className="card-img"
                            />
                            <div className="card-title-overlay">
                              <Card.Title>{card.title}</Card.Title>
                            </div>
                          </div>
                        </Card>
                      </Col>
                    ))}
                  </Row>
                </Container>
              </Carousel.Item>
            ))}
          </Carousel>
        </div>
      </Container>
    </>
  );
};

export default Newfilm;
