import React, { useState, useEffect } from "react";
import "../css/header.css"; // Import CSS for the header

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <header className={`header ${isScrolled ? "scrolled" : ""}`}>
      <div className="header-content">
        <div className="logo">
          <a href="/">Moviez</a>
        </div>
        <nav>
          <ul className="nav-links">
            <li>
              <a href="#home">Trang chủ</a>
            </li>
            <li>
              <a href="#movies">Phim</a>
            </li>
            <li>
              <a href="#popular">Mới & Phổ biến</a>
            </li>
            <li>
              <a href="#mylist">Danh sách của tôi</a>
            </li>
            <li>
              <a href="#language">Duyệt tìm theo ngôn ngữ</a>
            </li>
          </ul>
        </nav>
        <div className="header-icons">
          <a href="#search">
            <i className="fas fa-search"></i>
          </a>
          <a href="#notifications">
            <i className="fas fa-bell"></i>
          </a>
          <a href="#logout" className="logout-button">
            Log out
          </a>
        </div>
      </div>
    </header>
  );
};

export default Header;
