import React, { useEffect, useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import "../css/film.css"; // Import your CSS file
import { useParams } from "react-router-dom";
import axios from "axios";

const Film = () => {
  const { name } = useParams();
  const [film, setFilm] = useState(null);

  useEffect(() => {
    axios
      .get(`https://phim.nguonc.com/api/film/${name}`)
      .then((response) => {
        setFilm(response.data.movie);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [name]);

  if (!film) {
    return <p>Loading...</p>;
  }

  return (
    <>
      <Header />
      <div className="film-container">
        <div className="episode-list">
          <ul>
            {film.episodes.map((episode, index) => (
              <li key={index}>
                <p>Tập {episode.items[0].name}</p>
                <iframe
                  title={`Tập ${episode.items[0].name}`}
                  width="560"
                  height="315"
                  src={episode.items[0].embed}
                  allowFullScreen
                ></iframe>
              </li>
            ))}
          </ul>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Film;
