import axios from "axios";
import React, { useEffect, useState, useRef, useCallback } from "react";

const Listfilm = () => {
  const [films, setFilms] = useState([]);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const observer = useRef();

  const lastFilmElementRef = useCallback(
    (node) => {
      if (observer.current) observer.current.disconnect();
      observer.current = new IntersectionObserver((entries) => {
        if (entries[0].isIntersecting && hasMore) {
          setPage((prevPage) => prevPage + 1);
        }
      });
      if (node) observer.current.observe(node);
    },
    [hasMore]
  );

  useEffect(() => {
    const fetchFilms = async () => {
      try {
        const response = await axios.get(
          `https://phim.nguonc.com/api/films/danh-sach/phim-dang-chieu?page=${page}`
        );
        console.log(`Page ${page} API Response:`, response.data);
        if (response.data.items.length > 0) {
          setFilms((prevFilms) => [...prevFilms, ...response.data.items]);
        } else {
          setHasMore(false); // No more data to load
        }
      } catch (error) {
        console.log(error);
      }
    };

    fetchFilms();
  }, [page]);

  return (
    <div>
      <h1>Film List</h1>
      {films.map((film, index) => {
        if (films.length === index + 1) {
          return (
            <div ref={lastFilmElementRef} key={film.id}>
              {film.name} {index}
            </div>
          );
        } else {
          return (
            <div key={film.id}>
              {film.name} {index}
            </div>
          );
        }
      })}
      {hasMore && <div>Loading more...</div>}
    </div>
  );
};

export default Listfilm;
