import React, { useEffect, useState } from "react";
import "../css/contentdetailpage.css";
import { Col, Container, Row } from "react-bootstrap";
import { useParams } from "react-router-dom";
import axios from "axios";

const Contentdetailpage = () => {
  const { name } = useParams();
  const [film, setFilm] = useState({});

  useEffect(() => {
    axios
      .get(`https://phim.nguonc.com/api/film/${name}`)
      .then((response) => {
        setFilm(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [name]);

  return (
    <>
      <Container className="content-page">
        <Row>
          <Col className="col-4">
            <img
              src={film.image ? film.image : "./images/product1.jpg"}
              alt="film-poster"
            ></img>
          </Col>
          <Col className="col-8">
            <div className="content-details">
              <h1>{film.title ? film.title : "Tenki no Ko"}</h1>
              <h2>
                {film.quality ? film.quality : "HD"} ●{" "}
                {film.language ? film.language : "Vietsub"}
              </h2>
              <p>
                <strong>Số tập:</strong> {film.episodes ? film.episodes : "1"} |{" "}
                <strong>Hiện tại:</strong> {film.status ? film.status : "FULL"}
              </p>
              <p>
                <strong>Thể loại:</strong>{" "}
                {film.genre ? film.genre.join(", ") : "Hoạt Hình"}
              </p>
              <p className="white-color content-description">
                {film.description
                  ? film.description
                  : "Tokyo hiện đang trải qua những cơn mưa rào dường như làm gián đoạn nhịp sống thông thường của mọi người sống ở đó mãi mãi. Giữa cơn mưa như trút nước dường như vĩnh cửu này, cậu học sinh trung học bỏ trốn Hodaka Morishima đến, người đang phải vật lộn để tự trang trải tài chính cho bản thân kết thúc bằng một công việc tại một nhà xuất bản nhỏ. Đồng thời, cô gái mồ côi Hina Amano cũng cố gắng tìm việc làm để nuôi sống bản thân và em trai."}
              </p>
              <p>
                Casts:{" "}
                <strong className="white-color">
                  {film.casts ? film.casts.join(", ") : "Đang cập nhật"}
                </strong>
              </p>
              <p>
                Director:{" "}
                <strong className="white-color">
                  {film.director ? film.director : "Đang cập nhật"}
                </strong>
              </p>
              <a className="btn play-btn my-4" href="/">
                <i className="bi bi-play-fill"></i> Start
              </a>
            </div>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Contentdetailpage;
