import React, { useEffect, useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import "../css/blogpage.css";
import { Button, Col, Container, Row } from "react-bootstrap";
import axios from "axios";

const Blogpage = () => {
  const [blog, setBlog] = useState([]);
  const [top3item, setTop3item] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:9999/posts")
      .then((response) => {
        setBlog(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await axios.get('http://localhost:3000/items'); // Adjust URL as per your JSON-Server setup
        const sortedItems = response.data.sort((a, b) => b.id - a.id); // Sort by id in descending order (newest first)
        setItems(sortedItems.slice(0, 3)); // Get top 3 newest items based on id
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };

    fetchItems();
  }, []);

  console.log(blog);

  return (
    <>
      <Header />
      <Container className="blogpage-content my-4">
        <Row>
          <Col className="col-9">
            <Row className="d-flex justify-content-between">
              <Col>
                <h2>All Articles</h2>
              </Col>
              <Col>
                <div className="search-container">
                  <input
                    type="text"
                    className="search-input"
                    placeholder="Search..."
                  />
                  <span className="search-icon">
                    <i className="bi bi-search"></i>
                  </span>
                </div>
              </Col>
            </Row>
            <Row>
              {blog.map((post) => (
                <Col className="col-6" key={post.id}>
                  <div className="card">
                    <img
                      src={post.img_url}
                      className="card-img-top"
                      alt="img"
                    />
                    <div className="card-body">
                      <h5 className="card-title">
                        <strong>{post.title}</strong>
                      </h5>
                      <p className="card-text">{post.content}</p>
                      <a href="#" className="btn btn-primary">
                        Read more
                      </a>
                    </div>
                  </div>
                </Col>
              ))}
            </Row>
          </Col>
          <Col className="col-3">
            <Row>
              <h2>Recent Posts</h2>
              {/* You can add recent posts logic here */}
            </Row>
            <Row>
              <h2>Tags</h2>
              {/* You can add tags logic here */}
            </Row>
          </Col>
        </Row>
      </Container>
      <Footer />
    </>
  );
};

export default Blogpage;
