import axios from "axios";
import React, { useEffect, useState } from "react";

const Listfilm = () => {
  const [film, setFilm] = useState([]);
  const filmNames = [1, 2];

    useEffect(() => {
      axios
        .get("https://phim.nguonc.com/api/films/quoc-gia/au-my?page=1")
        .then((response) => {
          setFilm(response.data.items);
        })
        .catch((error) => {
          console.log(error);
        });
    }, []);

  useEffect(() => {
    // Create an array of promises for each API request
    const fetchFilms = filmNames.map((name) =>
      axios
        .get(`https://phim.nguonc.com/api/films/quoc-gia/au-my?page=${name}`)
        .then((response) => response.data.movie)
    );

    // Use Promise.all to wait for all the requests to complete
    Promise.all(fetchFilms)
      .then((filmsData) => {
        setFilm(filmsData);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [filmNames]);

  console.log(film);

  return (
    <>
      <h1>hello world</h1>
    </>
  );
};

export default Listfilm;
