import React, { useCallback, useEffect, useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import "../css/blogpage.css";
import { Button, Col, Container, Row } from "react-bootstrap";
import axios from "axios";
import { Link } from "react-router-dom";
import Filterbycheckbox from "./Filterbycheckbox";
import Filterbyradio from "./Filterbyradio";

const Blogpage = () => {
  const [blog, setBlog] = useState([]);
  const [top3item, setTop3item] = useState([]);
  const [queryCheckbox, setQueryCheckbox] = useState("");
  const [queryRadio, setQueryRadio] = useState("");

  useEffect(() => {
    // Fetch all blog posts
    axios
      .get("http://localhost:9999/posts")
      .then((response) => {
        setBlog(response.data);
      })
      .catch((error) => {
        console.log(error);
      });

    // Fetch top 3 newest posts
    const fetchTop3Items = async () => {
      try {
        const response = await axios.get("http://localhost:9999/posts");
        const sortedItems = response.data.sort((a, b) => b.id - a.id);
        setTop3item(sortedItems.slice(0, 3));
      } catch (error) {
        console.error("Error fetching top 3 items:", error);
      }
    };

    fetchTop3Items();
  }, []);

  const handleQuery = useCallback((sql) => {
    setQueryCheckbox(sql);
  }, []);

  const handleQueryCheckbox = useCallback((sql) => {
    setQueryRadio(sql);
  }, []);

  useEffect(() => {
    axios
      .get(`http://localhost:9999/posts/?${queryCheckbox}`)
      .then((response) => {
        setBlog(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [queryCheckbox]);

  useEffect(() => {
    axios
      .get(`http://localhost:9999/posts/?${queryRadio}`)
      .then((response) => {
        setBlog(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [queryRadio]);

  useEffect(() => {
    const combinedSql = [queryCheckbox, que].filter(Boolean).join("&");
    const url = combinedSql
      ? `http://localhost:9999/todo/?${combinedSql}`
      : "http://localhost:9999/todo";

    axios
      .get(url)
      .then((response) => {
        setTodo(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [sqlSidebar, sqlSubsidebar]);

  return (
    <>
      <Header />
      <Container className="blogpage-content my-4">
        <Row>
          <Col md={9}>
            <div className="d-flex justify-content-between align-items-center">
              <h2>All Articles</h2>
              <div className="search-container">
                <input
                  type="text"
                  className="search-input"
                  placeholder="Search..."
                />
                <span className="search-icon">
                  <i className="bi bi-search"></i>
                </span>
              </div>
            </div>
            <Row className="mt-4">
              {blog.map((post) => (
                <Col key={post.id} lg={6} className="mb-4">
                  <div className="card h-100">
                    <img
                      src={post.img_url}
                      className="card-img-top"
                      alt="Blog post"
                    />
                    <div className="card-body">
                      <h5 className="card-title">{post.title}</h5>
                      <p className="card-text">{post.content}</p>
                      <a href="#" className="btn btn-primary">
                        Read more
                      </a>
                    </div>
                  </div>
                </Col>
              ))}
            </Row>
          </Col>
          <Col md={3}>
            <div className="tags">
              <h2>Tags</h2>
              <Filterbycheckbox query={handleQuery} />
            </div>
            <div className="tags">
              <h2>Author</h2>
              <Filterbyradio query={handleQueryCheckbox} />
            </div>
            <div className="recent-posts">
              <h2>Recent Posts</h2>
              {top3item.map((post) => (
                <div key={post.id} className="mb-3">
                  <div className="card">
                    <img
                      src={post.img_url}
                      className="card-img-top"
                      alt="Recent post"
                    />
                    <div className="card-body">
                      <h5 className="card-title">{post.title}</h5>
                      <Link to={`/`} className="btn btn-primary">
                        Read more
                      </Link>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </Col>
        </Row>
      </Container>
      <Footer />
    </>
  );
};

export default Blogpage;
