            console.log(error);
          });
      }, []);
  return (
    <>
      <Header />
      <Container className="blogpage-content my-4">
        <Row>
          <Col className="col-8">
            <Row className="d-flex justify-content-between">
              <Col>
                <h2>All Articles</h2>
              </Col>
              <Col>
                <div class="search-container">
                  <input
                    type="text"
                    class="search-input"
                    placeholder="Search..."
                  />
                  <span class="search-icon">
                    <i class="bi bi-search"></i>
                  </span>
                </div>
              </Col>
            </Row>
            <Row>

            </Row>
          </Col>
