import React, { useState, useEffect } from "react";
import "../css/header.css"; // Create and import CSS for the header
import { Container, Form, FormControl, Button } from "react-bootstrap";
import axios from "axios";

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [search, setSearch] = useState("");

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  useEffect(() => {
    axios
      .get(`https://phim.nguonc.com/api/film/${name}`)
      .then((response) => {
        setFilm(response.data.movie);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [name]);

  return (
    <>
      <header className={`header ${isScrolled ? "scrolled" : ""}`}>
        <Container className="d-flex align-items-center justify-content-between">
          <nav>
            <div className="logo">
              <a href="/">Netflix</a>
            </div>
            <ul>
              <li>
                <a href="#home">Home</a>
              </li>
              <li>
                <a href="#about">About</a>
              </li>
              <li>
                <a href="#services">Services</a>
              </li>
              <li>
                <a href="#contact">Contact</a>
              </li>
            </ul>
          </nav>
          <nav className="right-side d-flex align-items-center">
            <div className="search-login-container d-flex align-items-center">
              <Form className="d-flex align-items-center">
                <FormControl
                  type="text"
                  placeholder="Search"
                  className="mr-sm-2"
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                />
              </Form>

              <Button variant="outline-light" className="ml-2">
                Login
              </Button>
            </div>
          </nav>
        </Container>
      </header>
    </>
  );
};

export default Header;
