import axios from "axios";
import React, { useEffect, useState, useRef, useCallback } from "react";
import { Card, Col, Container, Row } from "react-bootstrap";
import Header from "./Header";
import Footer from "./Footer";
import { Form, Link } from "react-router-dom";

const Listfilm = () => {
  const [films, setFilms] = useState([]);
  const [category, setCategory] = useState()
  const [page, setPage] = useState(1);
  const observer = useRef();
  const [selectedOption, setSelectedOption] = useState("");

  const handleSelectChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const lastFilmElementRef = useCallback((node) => {
    if (observer.current) observer.current.disconnect();
    observer.current = new IntersectionObserver((entries) => {
      if (entries[0].isIntersecting) {
        setPage((prevPage) => prevPage + 1);
      }
    });
    if (node) observer.current.observe(node);
  }, []);

  useEffect(() => {
    axios
      .get("https://phim.nguonc.com/api/films/danh-sach/phim-dang-chieu?page=1")
      .then((response) => {
        setFilm(response.data.items);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  useEffect(() => {
    const fetchFilms = async () => {
      try {
        const response = await axios.get(
          `https://phim.nguonc.com/api/films/quoc-gia/au-my?page=${page}`
        );
        setFilms((prevFilms) => [...prevFilms, ...response.data.items]);
      } catch (error) {
        console.log(error);
      }
    };

    fetchFilms();
  }, [page]);

  console.log(films);
  console.log(page);

  return (
    <div>
      <Container>
        <Header />
        <h1 style={{ marginTop: "50px" }}>Phim:</h1>
        <Form>
          <Form.Group controlId="exampleForm.SelectCustom">
            <Form.Label>Select an option:</Form.Label>
            <Form.Control
              as="select"
              custom
              value={selectedOption}
              onChange={handleSelectChange}
            >
              <option value="">Select...</option>
              <option value="option1">Option 1</option>
              <option value="option2">Option 2</option>
              <option value="option3">Option 3</option>
              <option value="option4">Option 4</option>
            </Form.Control>
          </Form.Group>
        </Form>
        <Row xs={1} md={2} lg={4}>
          {films.map((film, index) => (
            <Col key={index} className="mb-4">
              <Card className="card">
                <div className="card-img-container">
                  <Link to={`/film/${film.slug}`}>
                    <Card.Img
                      variant="top"
                      src={film.thumb_url}
                      alt={film.name}
                      className="card-img"
                    />
                  </Link>
                  <div className="card-title-overlay">
                    <Link to={`/film/${film.slug}`}>
                      <Card.Title>{film.name}</Card.Title>
                    </Link>
                  </div>
                </div>
              </Card>
            </Col>
          ))}
          <div ref={lastFilmElementRef}></div>
        </Row>
        <Footer />
      </Container>
    </div>
  );
};

export default Listfilm;
