import axios from "axios";
import React, { useEffect, useState, useRef, useCallback } from "react";
import {
  Card,
  Col,
  Container,
  Row,
  Dropdown,
  DropdownButton,
} from "react-bootstrap";
import Header from "./Header";
import Footer from "./Footer";
import { Link } from "react-router-dom";
import "../css/listfilm.css";

const Listfilm = () => {
  const [films, setFilms] = useState([]);
  const [countries, setCountries] = useState([]);
  const [page, setPage] = useState(1);
  const observer = useRef();
  const [selectedCountry, setSelectedCountry] = useState("");

  const handleSelectChange = (event) => {
    setSelectedCountry(event);
    setFilms([]);
    setPage(1);
  };

  const lastFilmElementRef = useCallback((node) => {
    if (observer.current) observer.current.disconnect();
    observer.current = new IntersectionObserver((entries) => {
      if (entries[0].isIntersecting) {
        setPage((prevPage) => prevPage + 1);
      }
    });
    if (node) observer.current.observe(node);
  }, []);

  useEffect(() => {
    // Assuming you have an API endpoint to fetch countries
    axios
      .get("http://localhost:9999/countries")
      .then((response) => {
        setCountries(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  useEffect(() => {
    const fetchFilms = async () => {
      try {
        const response = await axios.get(
          `https://phim.nguonc.com/api/films/quoc-gia/${selectedCountry}?page=${page}`
        );
        setFilms((prevFilms) => [...prevFilms, ...response.data.items]);
      } catch (error) {
        console.log(error);
      }
    };

    if (selectedCountry) {
      fetchFilms();
    }
  }, [page, selectedCountry]);

  console.log(selectedCountry);

  return (
    <div>
      <Container>
        <Header />
        <Row style={{ marginTop: "60px" }}>
          <Col className="col-2">
            <h1>Phim:</h1>
            <DropdownButton
              className="custom-dropdown"
              id="dropdown-basic"
              title={
                selectedCountry
                  ? countries.find(
                      (country) => country.slug === selectedCountry
                    ).name
                  : "Quá»‘c gia"
              }
              onSelect={handleSelectChange}
            >
              {countries.map((country) => (
                <Dropdown.Item
                  key={country.id}
                  eventKey={country.slug}
                  style={{ color: "white" }}
                >
                  {country.name}
                </Dropdown.Item>
              ))}
            </DropdownButton>
          </Col>
          <Col className="col-10">
            <Row xs={1} md={2} lg={4}>
              {films.map((film, index) => (
                <Col key={index} className="mb-4">
                  <Card className="card">
                    <div className="card-img-container">
                      <Link to={`/film/${film.slug}`}>
                        <Card.Img
                          variant="top"
                          src={film.thumb_url}
                          alt={film.name}
                          className="card-img"
                        />
                      </Link>
                      <div className="card-title-overlay">
                        <Link to={`/film/${film.slug}`}>
                          <Card.Title>{film.name}</Card.Title>
                        </Link>
                      </div>
                    </div>
                  </Card>
                </Col>
              ))}
              <div ref={lastFilmElementRef}></div>
            </Row>
          </Col>
        </Row>
        <Footer />
      </Container>
    </div>
  );
};

export default Listfilm;
