import React, { useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

const Management = () => {
  const { auth } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    // Navigate to different routes based on user role
    if (auth.role) {
      switch (auth.role) {
        case "admin":
          navigate("/admin");
          break;
        case "user":
          navigate("/");
          break;
        default:
          navigate("/error"); // Navigate to an error page or default route
      }
    } else {
      navigate("/error"); // Navigate to an error page if no role is found
    }
  }, [auth.role, navigate]);

  // Render nothing directly in the component
  return null;
};

export default Management;
